script:
{
   data : {
     title: "我是字段端",
     index:0,
   },
    onLoad(){
        console.log('onLoad');
    },
    onDestroy(){
        console.log('onDestroy');
    },
   add:function(args) {
       this.proxy.index++;
       this.proxy.title = "点击事件"+this.proxy.index;
       console.log('点击事件'+this.proxy.title);
       return args;
   },

   refresh:function(){
        this.setState();
   }
}
