script:
{
    data:reactive({
        title: "我是字段端",
        index:0,
    }),
    onLoad(){
        console.log('onLoad');
    },
    onDestroy(){
        console.log('onDestroy');
    },
   add:function(args) {
       this.data.index++;
       this.data.title = "点击事件" + this.data.index;
       console.log('点击事件' + this.data.title);
       return args;
   },

   refresh:function(){
        this.setState();
   }
}
