script:
{
    data:reactive({
        title: "Form 表单",
        title2: "我来自js（点我）",
        index:0,
    }),
    onLoad(){
        console.log('onLoad');
    },
    onDestroy(){
        console.log('onDestroy');
    },
   ok:function(args) {
       alert('添加');
       this.data.index++;
       this.data.title = "点击事件" + this.data.index;
       console.log('点击事件' + this.data.title);
       return args;
   },
   cancel:function(args) {
       alert('取消');
       this.data.index++;
       this.data.title2 = "点击事件" + this.data.index;
       return args;
   },
   refresh:function(){
        this.setState();
   }
}
