script:
{
    data:reactive({
        dataList: ['我是字段端1','我是字段端2'],
        state: 0,
        index:0,
        userInfo: {
           name:'2222'
        },
    }),
    onLoad(){
        console.log('onLoad');
    },
    onDestroy(){
        console.log('onDestroy');
    },

    changeState(){
        if (this.data.state == 0){
            this.data.state = 1;
            this.data.userInfo.name = '1111';
        } else {
            this.data.state = 0;
            this.data.userInfo.name = '0000';
        }
        this.data.index++;
        this.data.dataList.push('--' + this.data.index + '--')
    },

}
