script:
	{
		data: reactive({
			userInfo: {
				name: '用户名称',
				age: 30,
				level: 1,
			},
			orderMenu: ['全部订单', '已支付', '待支付'],
			state: 0,
			index: 0,

		}),
		onLoad() {
			console.log('onLoad');
		},
		onDestroy() {
			console.log('onDestroy');
		},

		changeState() {
			if (this.data.state == 0) {
				this.data.state = 1;
				this.data.userInfo.name = '1111';
			} else {
				this.data.state = 0;
				this.data.userInfo.name = '0000';
			}
			//   this.data.index++;
			//   this.data.dataList.push('--' + this.data.index + '--')
		},

	}
